import './App.css';
import { useState } from 'react';

function App() {

  let post = '강남 우동 맛집';
  let [글제목, b] = useState('여자 코트 추천');
  // state를 참조하는 html은 state가 변경될 때 자동으로 재렌더링되어 반영된다.
  // 자주 변경될 것 같은 부분은 state로 만들어둬야 좋다.

  return (
    <div className="App">
      <div className='black-nav'>
        <h4 style={ {color : 'red', fontSize: '16px'} }>ReactBlog</h4>
      </div>
      <div className='list'>
        <h4>{ 글제목 }</h4>
        <p>2월 17일 발행</p>
      </div>
    </div>
  );
}

export default App;
